import { useState, useEffect, useRef } from 'react';

const KAKAO_API_KEY = '2b16e1d042f4324ca028c93ed0a54fa2';

export const useKakaoMap = (selectedRegion, regionCoordinates) => {
  const mapContainer = useRef(null);
  const [map, setMap] = useState(null);

  // 카카오 맵 초기화
  useEffect(() => {
    const script = document.createElement('script');
    script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${KAKAO_API_KEY}&autoload=false`;
    script.async = true;

    script.onload = () => {
      window.kakao.maps.load(() => {
        const coords = regionCoordinates[selectedRegion];
        const options = {
          center: new window.kakao.maps.LatLng(coords.lat, coords.lng),
          level: 8
        };
        const mapInstance = new window.kakao.maps.Map(mapContainer.current, options);
        setMap(mapInstance);
      });
    };

    document.head.appendChild(script);
    return () => {
      document.head.removeChild(script);
    };
  }, []);

  // 지역 변경시 지도 이동
  useEffect(() => {
    if (map) {
      const coords = regionCoordinates[selectedRegion];
      const moveLatLon = new window.kakao.maps.LatLng(coords.lat, coords.lng);
      map.panTo(moveLatLon);
    }
  }, [selectedRegion, map, regionCoordinates]);

  return { mapContainer, map };
};
