import { useState } from 'react';
import { regionCoordinates, roadkillData } from './constants/regionData';
import { useKakaoMap } from './hooks/useKakaoMap';
import Sidebar from './components/Sidebar';
import MapContainer from './components/MapContainer';
import RoadkillChart from './components/RoadkillChart';

export default function RoadkillAnalysisApp() {
  const [selectedRegion, setSelectedRegion] = useState('서울');

  const { mapContainer } = useKakaoMap(selectedRegion, regionCoordinates);

  const currentData = roadkillData[selectedRegion] || [];
  const totalCount = currentData.reduce((sum, item) => sum + item.count, 0);

  return (
    <div style={{ display: 'flex', height: '100vh', fontFamily: 'Arial, sans-serif' }}>
      {/* 사이드바 */}
      <Sidebar
        selectedRegion={selectedRegion}
        setSelectedRegion={setSelectedRegion}
        regionCoordinates={regionCoordinates}
        currentData={currentData}
        totalCount={totalCount}
      />

      {/* 메인 컨텐츠 */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        {/* 지도 */}
        <MapContainer
          mapContainer={mapContainer}
          selectedRegion={selectedRegion}
          regionCoordinates={regionCoordinates}
        />

        {/* 차트 */}
        <RoadkillChart
          currentData={currentData}
          selectedRegion={selectedRegion}
          regionCoordinates={regionCoordinates}
        />
      </div>
    </div>
  );
}
