import { useEffect, useRef, useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';

const FloatingChart = ({ visible, onClose, title, data }) => {
  const wrapperRef = useRef(null);
  const [dimensions, setDimensions] = useState({ w: 0, h: 0 });

  useEffect(() => {
    if (!visible) return;
    const measure = () => {
      const el = wrapperRef.current;
      if (!el) return;
      const rect = el.getBoundingClientRect();
      const w = Math.max(0, Math.floor(rect.width));
      const h = Math.max(0, Math.floor(rect.height));
      setDimensions({ w, h });
    };
    measure();
    const ro = typeof ResizeObserver !== 'undefined' ? new ResizeObserver(measure) : null;
    if (ro && wrapperRef.current) ro.observe(wrapperRef.current);
    window.addEventListener('resize', measure);
    return () => {
      window.removeEventListener('resize', measure);
      if (ro && wrapperRef.current) ro.unobserve(wrapperRef.current);
    };
  }, [visible]);

  if (!visible) return null;
  return (
    <div style={{
      position: 'absolute',
      bottom: '20px',
      right: '20px',
      width: '500px',
      height: '280px',
      minWidth: 0,
      minHeight: 0,
      backgroundColor: 'rgba(255, 255, 255, 0.95)',
      padding: '20px',
      borderRadius: '12px',
      boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
      backdropFilter: 'blur(10px)'
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
        <h2 style={{ fontSize: '16px', fontWeight: 'bold', color: '#2c3e50', margin: 0 }}>{title}</h2>
        <button
          onClick={onClose}
          style={{
            background: 'none',
            border: 'none',
            fontSize: '20px',
            cursor: 'pointer',
            color: '#7f8c8d',
            padding: '0 5px'
          }}
        >
          ×
        </button>
      </div>
      <div ref={wrapperRef} style={{ width: '100%', height: '85%', minWidth: 0, minHeight: 0 }}>
        {dimensions.w > 0 && dimensions.h > 0 && (
          <BarChart width={dimensions.w} height={dimensions.h} data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="animal" style={{ fontSize: '12px' }} />
            <YAxis style={{ fontSize: '12px' }} />
            <Tooltip />
            <Bar dataKey="count" fill="#e74c3c" name="발생 건수" />
          </BarChart>
        )}
      </div>
    </div>
  );
};

export default FloatingChart;
