import { useEffect, useRef, useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const FloatingChart = ({ visible, onClose, title, data }) => {
  const wrapperRef = useRef(null);
  const [ready, setReady] = useState(false);

  useEffect(() => {
    if (!visible) return;
    const checkSize = () => {
      const el = wrapperRef.current;
      if (!el) return;
      const w = el.offsetWidth || 0;
      const h = el.offsetHeight || 0;
      setReady(w > 0 && h > 0);
    };
    checkSize();
    window.addEventListener('resize', checkSize);
    return () => window.removeEventListener('resize', checkSize);
  }, [visible]);

  if (!visible) return null;
  return (
    <div style={{
      position: 'absolute',
      bottom: '20px',
      right: '20px',
      width: '500px',
      height: '280px',
      minWidth: 0,
      minHeight: 0,
      backgroundColor: 'rgba(255, 255, 255, 0.95)',
      padding: '20px',
      borderRadius: '12px',
      boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
      backdropFilter: 'blur(10px)'
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
        <h2 style={{ fontSize: '16px', fontWeight: 'bold', color: '#2c3e50', margin: 0 }}>{title}</h2>
        <button
          onClick={onClose}
          style={{
            background: 'none',
            border: 'none',
            fontSize: '20px',
            cursor: 'pointer',
            color: '#7f8c8d',
            padding: '0 5px'
          }}
        >
          ×
        </button>
      </div>
      <div ref={wrapperRef} style={{ width: '100%', height: '85%', minWidth: 0, minHeight: 0 }}>
        {ready && (
          <ResponsiveContainer width="100%" height="100%">
            <BarChart data={data}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="animal" style={{ fontSize: '12px' }} />
              <YAxis style={{ fontSize: '12px' }} />
              <Tooltip />
              <Bar dataKey="count" fill="#e74c3c" name="발생 건수" />
            </BarChart>
          </ResponsiveContainer>
        )}
      </div>
    </div>
  );
};

export default FloatingChart;
