import { useState, useEffect, useRef } from 'react';

export default function KakaoMapApp() {
  const mapContainer = useRef(null);
  const [map, setMap] = useState(null);
  const [coordinates, setCoordinates] = useState({
    lat: 33.450701,
    lng: 126.570667
  });
  const [inputLat, setInputLat] = useState('33.450701');
  const [inputLng, setInputLng] = useState('126.570667');
  const [level, setLevel] = useState(3);
  const [appKey, setAppKey] = useState('2b16e1d042f4324ca028c93ed0a54fa2');
  const [isScriptLoaded, setIsScriptLoaded] = useState(false);

  // 카카오 맵 스크립트 로드
  useEffect(() => {
    if (!appKey) return;

    const script = document.createElement('script');
    script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${appKey}&libraries=services,clusterer,drawing&autoload=false`;
    script.async = true;

    script.onload = () => {
      window.kakao.maps.load(() => {
        setIsScriptLoaded(true);
      });
    };

    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, [appKey]);

  // 지도 초기화
  useEffect(() => {
    if (!isScriptLoaded || !mapContainer.current) return;

    const options = {
      center: new window.kakao.maps.LatLng(coordinates.lat, coordinates.lng),
      level: level
    };

    const mapInstance = new window.kakao.maps.Map(mapContainer.current, options);
    setMap(mapInstance);

    // 마커 추가
    const marker = new window.kakao.maps.Marker({
      position: new window.kakao.maps.LatLng(coordinates.lat, coordinates.lng)
    });
    marker.setMap(mapInstance);
  }, [isScriptLoaded, coordinates.lat, coordinates.lng, level]);

  const handleMoveToCoordinate = () => {
    const lat = parseFloat(inputLat);
    const lng = parseFloat(inputLng);

    if (!isNaN(lat) && !isNaN(lng)) {
      setCoordinates({ lat, lng });
    }
  };

  const handleZoomIn = () => {
    if (map) {
      const currentLevel = map.getLevel();
      map.setLevel(currentLevel - 1);
      setLevel(currentLevel - 1);
    }
  };

  const handleZoomOut = () => {
    if (map) {
      const currentLevel = map.getLevel();
      map.setLevel(currentLevel + 1);
      setLevel(currentLevel + 1);
    }
  };

  const presetLocations = [
    { name: '제주도', lat: 33.450701, lng: 126.570667 },
    { name: '서울 시청', lat: 37.5665, lng: 126.9780 },
    { name: '부산 해운대', lat: 35.1584, lng: 129.1604 },
    { name: '판교', lat: 37.3952969, lng: 127.1104492 }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-yellow-50 to-yellow-100 p-8">
      <div className="max-w-6xl mx-auto">
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
          {/* 헤더 */}
          <div className="bg-gradient-to-r from-yellow-400 to-yellow-500 p-6">
            <h1 className="text-3xl font-bold text-white mb-2">
              🗺️ Kakao 지도 React 앱
            </h1>
            <p className="text-yellow-50">
              카카오 지도 API를 React로 구현한 예제입니다
            </p>
          </div>

          <div className="p-6">
            {/* API 키 확인 */}
            {appKey === '2b16e1d042f4324ca028c93ed0a54fa2E' && (
              <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
                <p className="font-semibold text-red-700 mb-2">⚠️ API 키를 설정해주세요</p>
                <p className="text-sm text-red-600">
                  코드의 <code className="bg-red-100 px-1 rounded">appKey</code> 변수에 카카오 JavaScript 키를 입력하세요.
                </p>
              </div>
            )}

            {/* API 키 입력 */}
            {!isScriptLoaded && appKey !== '2b16e1d042f4324ca028c93ed0a54fa2' && (
              <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                <p className="text-blue-700">🔄 지도를 로딩중입니다...</p>
              </div>
            )}

            {isScriptLoaded && (
              <>
                {/* 컨트롤 패널 */}
                <div className="grid md:grid-cols-2 gap-6 mb-6">
                  {/* 좌표 입력 */}
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <h3 className="font-semibold text-gray-700 mb-3">📍 좌표로 이동</h3>
                    <div className="space-y-3">
                      <input
                        type="number"
                        step="any"
                        placeholder="위도 (Latitude)"
                        value={inputLat}
                        onChange={(e) => setInputLat(e.target.value)}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-yellow-400"
                      />
                      <input
                        type="number"
                        step="any"
                        placeholder="경도 (Longitude)"
                        value={inputLng}
                        onChange={(e) => setInputLng(e.target.value)}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-yellow-400"
                      />
                      <button
                        onClick={handleMoveToCoordinate}
                        className="w-full bg-yellow-400 hover:bg-yellow-500 text-white font-semibold py-2 rounded-lg transition"
                      >
                        이동하기
                      </button>
                    </div>
                  </div>

                  {/* 빠른 이동 */}
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <h3 className="font-semibold text-gray-700 mb-3">⚡ 빠른 이동</h3>
                    <div className="grid grid-cols-2 gap-2">
                      {presetLocations.map((loc) => (
                        <button
                          key={loc.name}
                          onClick={() => {
                            setCoordinates({ lat: loc.lat, lng: loc.lng });
                            setInputLat(loc.lat.toString());
                            setInputLng(loc.lng.toString());
                          }}
                          className="bg-white hover:bg-yellow-50 border border-gray-300 hover:border-yellow-400 px-3 py-2 rounded-lg text-sm font-medium transition"
                        >
                          {loc.name}
                        </button>
                      ))}
                    </div>
                  </div>
                </div>

                {/* 지도 컨테이너 */}
                <div className="relative">
                  <div
                    ref={mapContainer}
                    className="w-full h-[500px] rounded-lg shadow-md"
                  />

                  {/* 줌 컨트롤 */}
                  <div className="absolute top-4 right-4 bg-white rounded-lg shadow-lg overflow-hidden">
                    <button
                      onClick={handleZoomIn}
                      className="block w-10 h-10 hover:bg-gray-100 border-b border-gray-200 flex items-center justify-center text-xl font-bold"
                      title="확대"
                    >
                      +
                    </button>
                    <button
                      onClick={handleZoomOut}
                      className="block w-10 h-10 hover:bg-gray-100 flex items-center justify-center text-xl font-bold"
                      title="축소"
                    >
                      −
                    </button>
                  </div>
                </div>

                {/* 정보 표시 */}
                <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                  <div className="grid md:grid-cols-3 gap-4 text-sm">
                    <div>
                      <span className="font-semibold text-gray-600">위도:</span>{' '}
                      <span className="text-gray-800">{coordinates.lat.toFixed(6)}</span>
                    </div>
                    <div>
                      <span className="font-semibold text-gray-600">경도:</span>{' '}
                      <span className="text-gray-800">{coordinates.lng.toFixed(6)}</span>
                    </div>
                    <div>
                      <span className="font-semibold text-gray-600">줌 레벨:</span>{' '}
                      <span className="text-gray-800">{level}</span>
                    </div>
                  </div>
                </div>

                {/* 카카오 지도 링크 */}
                <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
                  <h3 className="font-semibold text-gray-700 mb-2">🔗 카카오 지도에서 보기</h3>
                  <div className="flex flex-wrap gap-2">
                    <a
                      href={`https://map.kakao.com/link/map/${coordinates.lat},${coordinates.lng}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="inline-block bg-white border border-gray-300 hover:border-yellow-400 px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      지도 보기
                    </a>
                    <a
                      href={`https://map.kakao.com/link/to/목적지,${coordinates.lat},${coordinates.lng}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="inline-block bg-white border border-gray-300 hover:border-yellow-400 px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      길찾기
                    </a>
                    <a
                      href={`https://map.kakao.com/link/roadview/${coordinates.lat},${coordinates.lng}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="inline-block bg-white border border-gray-300 hover:border-yellow-400 px-4 py-2 rounded-lg text-sm font-medium transition"
                    >
                      로드뷰
                    </a>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
