const Sidebar = ({ selectedRegion, setSelectedRegion, regionCoordinates, currentData, totalCount }) => {
  return (
    <div style={{ 
      width: '400px', 
      backgroundColor: '#2c3e50', 
      color: 'white',
      padding: '20px',
      overflowY: 'auto'
    }}>
      <h1 style={{ fontSize: '24px', marginBottom: '10px', fontWeight: 'bold' }}>
        🦌 전국 로드킬 분석
      </h1>
      <p style={{ fontSize: '14px', color: '#bdc3c7', marginBottom: '30px' }}>
        지역별 동물 로드킬 발생 현황
      </p>

      {/* 지역 선택 */}
      <div style={{ marginBottom: '30px' }}>
        <label style={{ display: 'block', marginBottom: '10px', fontSize: '14px', fontWeight: 'bold' }}>
          📍 지역 선택
        </label>
        <select
          value={selectedRegion}
          onChange={(e) => setSelectedRegion(e.target.value)}
          style={{
            width: '100%',
            padding: '12px',
            fontSize: '16px',
            borderRadius: '8px',
            border: 'none',
            backgroundColor: '#34495e',
            color: 'white',
            cursor: 'pointer'
          }}
        >
          {Object.keys(regionCoordinates).map((region) => (
            <option key={region} value={region}>
              {regionCoordinates[region].name}
            </option>
          ))}
        </select>
      </div>

      {/* 통계 정보 */}
      <div style={{
        backgroundColor: '#34495e',
        padding: '15px',
        borderRadius: '8px',
        marginBottom: '20px'
      }}>
        <div style={{ fontSize: '14px', color: '#bdc3c7', marginBottom: '5px' }}>
          총 로드킬 발생 건수
        </div>
        <div style={{ fontSize: '32px', fontWeight: 'bold', color: '#e74c3c' }}>
          {totalCount.toLocaleString()}건
        </div>
      </div>

      {/* 동물별 순위 */}
      <div style={{ marginTop: '20px' }}>
        <h3 style={{ fontSize: '16px', marginBottom: '15px', fontWeight: 'bold' }}>
          동물별 발생 건수
        </h3>
        {currentData.map((item, index) => (
          <div key={item.animal} style={{
            backgroundColor: '#34495e',
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '8px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
              <span style={{
                backgroundColor: index === 0 ? '#e74c3c' : '#95a5a6',
                width: '24px',
                height: '24px',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
                fontWeight: 'bold'
              }}>
                {index + 1}
              </span>
              <span style={{ fontSize: '14px' }}>{item.animal}</span>
            </div>
            <span style={{ fontSize: '16px', fontWeight: 'bold', color: '#e74c3c' }}>
              {item.count}건
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
