import { useEffect, useRef } from 'react';

const MapContainer = ({ selectedProvince, selectedCity, locationData, apiKey }) => {
  const mapContainer = useRef(null);
  const mapInstance = useRef(null);

  // 카카오 맵 초기화
  useEffect(() => {
    const script = document.createElement('script');
    script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${apiKey}&autoload=false`;
    script.async = true;

    script.onload = () => {
      window.kakao.maps.load(() => {
        const coords = locationData[selectedProvince];
        const options = {
          center: new window.kakao.maps.LatLng(coords.lat, coords.lng),
          level: coords.level
        };
        mapInstance.current = new window.kakao.maps.Map(mapContainer.current, options);
      });
    };

    document.head.appendChild(script);
    return () => {
      if (document.head.contains(script)) {
        document.head.removeChild(script);
      }
    };
  }, [apiKey, selectedProvince, locationData]);

  // 지역 변경시 지도 이동 및 줌
  useEffect(() => {
    if (mapInstance.current) {
      let coords, level;

      if (selectedCity) {
        const cityData = locationData[selectedProvince].cities[selectedCity];
        coords = new window.kakao.maps.LatLng(cityData.lat, cityData.lng);
        level = cityData.level;
      } else {
        const provinceData = locationData[selectedProvince];
        coords = new window.kakao.maps.LatLng(provinceData.lat, provinceData.lng);
        level = provinceData.level;
      }

      mapInstance.current.setLevel(level);
      mapInstance.current.panTo(coords);
    }
  }, [selectedProvince, selectedCity, locationData]);

  return (
    <div ref={mapContainer} style={{ width: '100%', height: '100%' }} />
  );
};

export default MapContainer;
