import { useState } from 'react';
import { locationData, roadkillData } from '../constants/locationData';
import { useKakaoMap } from '../hooks/useKakaoMap';
import AdvancedSidebar from './AdvancedSidebar';
import FloatingChart from './FloatingChart';

export default function RoadkillAnalysisAdvanced() {
  const [selectedProvince, setSelectedProvince] = useState('ì„œìš¸');
  const [selectedCity, setSelectedCity] = useState(null);
  const [showChart, setShowChart] = useState(true);

  const apiKey = '2b16e1d042f4324ca028c93ed0a54fa2';

  const { mapContainer } = useKakaoMap({
    selectedProvince,
    selectedCity,
    locationData,
    apiKey
  });

  const dataKey = selectedCity ? `${selectedProvince}-${selectedCity}` : selectedProvince;
  const currentData = roadkillData[dataKey] || [];
  const totalCount = currentData.reduce((sum, item) => sum + item.count, 0);
  const currentLocationName = selectedCity 
    ? `${locationData[selectedProvince].name} ${selectedCity}`
    : locationData[selectedProvince].name;

  return (
    <div style={{ position: 'relative', width: '100%', height: '100vh' }}>
      <div ref={mapContainer} style={{ width: '100%', height: '100%' }} />

      <AdvancedSidebar
        selectedProvince={selectedProvince}
        setSelectedProvince={setSelectedProvince}
        selectedCity={selectedCity}
        setSelectedCity={setSelectedCity}
        locationData={locationData}
        currentData={currentData}
        currentLocationName={currentLocationName}
        totalCount={totalCount}
      />

      <FloatingChart
        visible={showChart}
        onClose={() => setShowChart(false)}
        title={`${currentLocationName} ë¡œë“œí‚¬ í˜„í™©`}
        data={currentData}
      />

      {!showChart && (
        <button
          onClick={() => setShowChart(true)}
          style={{
            position: 'absolute',
            bottom: '20px',
            right: '20px',
