import { useEffect, useRef, useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';

const BottomDockChart = ({ visible, title, data }) => {
  const outerRef = useRef(null);
  const innerRef = useRef(null);
  const [dims, setDims] = useState({ w: 0, h: 0 });

  useEffect(() => {
    if (!visible) return;
    const measure = () => {
      const el = innerRef.current;
      if (!el) return;
      const rect = el.getBoundingClientRect();
      setDims({ w: Math.max(0, Math.floor(rect.width)), h: Math.max(0, Math.floor(rect.height)) });
    };
    measure();
    const ro = typeof ResizeObserver !== 'undefined' ? new ResizeObserver(measure) : null;
    if (ro && innerRef.current) ro.observe(innerRef.current);
    window.addEventListener('resize', measure);
    return () => {
      window.removeEventListener('resize', measure);
      if (ro && innerRef.current) ro.unobserve(innerRef.current);
    };
  }, [visible]);

  if (!visible) return null;

  return (
    <div ref={outerRef} style={{
      position: 'fixed',
      left: 0,
      right: 0,
      bottom: 0,
      height: '280px',
      backgroundColor: 'rgba(255, 255, 255, 0.98)',
      boxShadow: '0 -4px 20px rgba(0,0,0,0.2)',
      zIndex: 2147483600
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '12px 16px' }}>
        <h2 style={{ margin: 0, fontSize: '16px', fontWeight: 'bold', color: '#2c3e50' }}>{title}</h2>
      </div>
      <div className="chart" style={{ position: 'relative', height: 'calc(100% - 48px)' }}>
        <div ref={innerRef} style={{ position: 'absolute', inset: 0 }}>
          {dims.w > 0 && dims.h > 0 && (
            <BarChart width={dims.w} height={dims.h} data={data}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="animal" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="count" fill="#e74c3c" name="발생 건수" />
            </BarChart>
          )}
        </div>
      </div>
    </div>
  );
};

export default BottomDockChart;
