import { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const RoadkillChart = ({ isOpen, onToggle, currentLocationName, currentData }) => {
  const [isReady, setIsReady] = useState(false);

  useEffect(() => {
    if (isOpen) {
      // DOM이 렌더링된 후 차트를 표시
      const timer = setTimeout(() => setIsReady(true), 100);
      return () => clearTimeout(timer);
    } else {
      setIsReady(false);
    }
  }, [isOpen]);

  const barColors = ['#cccccc', '#999999', '#666666', '#444444', '#222222']; // 5단계 그레이스케일

  return (
    <>
      {/* Floating 차트 */}
      {isOpen && (
        <div style={{
          position: 'absolute',
          bottom: '20px',
          right: '20px',
          width: '520px',
          height: '300px',
          backgroundColor: 'rgba(255, 255, 255, 0.95)',
          padding: '20px',
          borderRadius: '12px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
          backdropFilter: 'blur(10px)',
          zIndex: 1100
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
            <h2 style={{ fontSize: '16px', fontWeight: 'bold', color: '#2c3e50', margin: 0 }}>
              {currentLocationName} 로드킬 현황
            </h2>
            <button
              onClick={onToggle}
              style={{
                background: 'none',
                border: 'none',
                fontSize: '20px',
                cursor: 'pointer',
                color: '#7f8c8d',
                padding: '0 5px'
              }}
            >
              ×
            </button>
          </div>
          <div style={{ width: '480px', height: '220px' }}>
            {isReady && currentData.length > 0 ? (
              <ResponsiveContainer width={480} height={220}>
                <BarChart data={currentData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="animal" style={{ fontSize: '12px' }} />
                  <YAxis style={{ fontSize: '12px' }} />
                  <Tooltip />
                  {currentData.map((entry, idx) => (
                    <Bar key={idx} dataKey="count" fill={barColors[idx % barColors.length]} name={entry.animal} data={[entry]} xAxisId="0" />
                  ))}
                </BarChart>
              </ResponsiveContainer>
            ) : (
              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'center', 
                height: '100%',
                color: '#7f8c8d',
                fontSize: '14px'
              }}>
                데이터 로딩 중...
              </div>
            )}
          </div>
        </div>
      )}

      {/* 차트 토글 버튼 */}
      {!isOpen && (
        <button
          onClick={onToggle}
          style={{
            position: 'absolute',
            bottom: '20px',
            right: '20px',
            backgroundColor: '#e74c3c',
            color: 'white',
            border: 'none',
            padding: '12px 20px',
            borderRadius: '8px',
            fontSize: '14px',
            fontWeight: 'bold',
            cursor: 'pointer',
            boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
            zIndex: 1100
          }}
        >
          📊 차트 보기
        </button>
      )}
    </>
  );
};

export default RoadkillChart;
